import React, { useState } from 'react';
import injectMenuStyles from './styles';

injectMenuStyles();

type View = 'criar' | 'responder' | 'resultados' | 'respondentes';

export default function Menu({ current, onChange }: { current: View; onChange: (v: View) => void }) {
  const [collapsed, setCollapsed] = useState(false);

  const items: { key: View; icon: string; label: string }[] = [
    { key: 'responder', icon: 'üìù', label: 'Responder' },
    { key: 'criar', icon: '‚úèÔ∏è', label: 'Criar' },
    { key: 'resultados', icon: 'üìä', label: 'Resultados' },
    { key: 'respondentes', icon: 'üë•', label: 'Respondentes' },
  ];

  return (
    <aside className={`sr-menu ${collapsed ? 'collapsed' : 'expanded'}`}>
      <div className="sr-top">
        <div className="sr-brand">
          <div className="logo" />
          <div className="brand-text">Question√°rios</div>
        </div>
        <button className="sr-toggle" onClick={() => setCollapsed(c => !c)} aria-label={collapsed ? 'Expandir menu' : 'Recolher menu'}>
          {collapsed ? '‚ñ∂' : '‚óÄ'}
        </button>
      </div>

      <nav className="sr-nav">
        {items.map(item => (
          <button key={item.key} className={current === item.key ? 'nav-item active' : 'nav-item'} onClick={() => onChange(item.key)}>
            <span className="icon" aria-hidden>{item.icon}</span>
            <span className="label">{item.label}</span>
          </button>
        ))}
      </nav>

      <div className="sr-footer">SEBRAE</div>
    </aside>
  );
}
