const css = `
:root{
  --sr-blue: #004b8d;
  --sr-accent: #ffd200;
  --sr-bg: #f6f9fc;
  --sr-surface: #ffffff;
}
.logon-root{ display:flex; align-items:center; justify-content:center; min-height:100vh; background:var(--sr-bg); padding:24px }
.logon-card{ width:100%; max-width:480px; background:var(--sr-surface); padding:20px; border-radius:8px; box-shadow:0 6px 18px rgba(10,10,20,0.06) }
.logon-card h2{ color:var(--sr-blue); margin-top:0 }
.logon-card .field label{ display:block; margin-bottom:6px }
.logon-card .field input{ width:100%; padding:10px; border-radius:6px; border:1px solid #e3e8ee }
.logon-card .actions{ margin-top:12px }
.primary{ background:var(--sr-blue); color:white; border:none; padding:10px 14px; border-radius:6px; cursor:pointer }
`;

let injected = false;
export default function injectLogonStyles(){
  if (injected) return;
  injected = true;
  const el = document.createElement('style');
  el.setAttribute('data-styles','logon');
  el.innerHTML = css;
  document.head.appendChild(el);
}
