const css = `
.sr-header{ display:flex; align-items:center; justify-content:space-between; gap:16px; padding:18px 24px; background:linear-gradient(90deg,var(--sr-blue),#1166b3); color:white }
.sr-header .brand{ display:flex; align-items:center; gap:12px; font-weight:700 }
.sr-header .brand .logo{ width:36px; height:36px; border-radius:6px; background:var(--sr-accent); display:inline-block }
.sr-header .title{ font-size:18px }
.sr-header .actions{ display:flex; gap:12px; align-items:center }
.sr-header .user{ background:rgba(255,255,255,0.12); padding:6px 10px; border-radius:18px }
@media (max-width:720px){ .sr-header{ flex-direction:column; align-items:flex-start } }
`;

let injected = false;
export default function injectHeaderStyles(){
  if (injected) return;
  injected = true;
  const el = document.createElement('style');
  el.setAttribute('data-styles','shared-header');
  el.innerHTML = css;
  document.head.appendChild(el);
}
