}

@media (max-width: 768px) {
  .form-grid {
    grid-template-columns: 1fr;
  }

  .perfil-container {
    padding: 16px;
  }

  .perfil-card {
    padding: 20px;
  }

  .avatar-section {
    flex-direction: column;
    align-items: flex-start;
  }
}
