import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import Menu from './pages/menu/index';
import QuestionarioPage from './pages/questionario/index';
import Main from './pages/main/index';
import Logon from './pages/login/index';
import Header from './pages/shared/header/index';
import Footer from './pages/shared/footer/index';
import type { Question } from './pages/questionario/index';

type View = 'criar' | 'responder' | 'resultados' | 'respondentes' | 'login';

type User = { name: string; token?: string } | null;

function App() {
  const [view, setView] = useState<View>('responder');
  const [questions, setQuestions] = useState<Question[]>([]);
  const [responses, setResponses] = useState<any[]>([]);
  const [user, setUser] = useState<User>(() => {
    try {
      const raw = localStorage.getItem('sr_user');
      return raw ? JSON.parse(raw) as User : null;
    } catch (e) {
      return null;
    }
  });

  function handleSave(qs: Question[]) {
    setQuestions(qs);
    setView('responder');
  }

  function handleSubmitResponse(resp: any) {
    setResponses(prev => [resp, ...prev]);
    setView('resultados');
  }

  const navigate = useNavigate();

  function pathFor(v: View | string) {
    switch (v) {
      case 'criar': return '/criar';
      case 'responder': return '/';
      case 'resultados': return '/resultados';
      case 'respondentes': return '/respondentes';
      default: return '/';
    }
  }

  function handleLogout() {
    setUser(null);
    try { localStorage.removeItem('sr_user'); } catch(e) {}
    navigate('/');
  }

  return (
    <div className="sr-app">
      <Header subtitle="Crie e responda questionÃ¡rios com facilidade" onLogin={() => navigate('/login')} user={user} onLogout={handleLogout} />
      <div style={{ display: 'flex', alignItems: 'stretch', flex: 1 }}>
        <Menu current={''} onChange={v => navigate(pathFor(v))} />
        <main className="sr-main">
          <Routes>
            <Route path="/login" element={<Logon onLogin={(u) => { setUser(u); try { localStorage.setItem('sr_user', JSON.stringify(u)); } catch(e){} navigate('/'); }} />} />
            <Route path="/criar" element={<QuestionarioPage view={'criar'} setView={(v)=>navigate(pathFor(v))} questions={questions} setQuestions={setQuestions} responses={responses} addResponse={handleSubmitResponse} />} />
            <Route path="/resultados" element={<QuestionarioPage view={'resultados'} setView={(v)=>navigate(pathFor(v))} questions={questions} setQuestions={setQuestions} responses={responses} addResponse={handleSubmitResponse} />} />
            <Route path="/respondentes" element={<QuestionarioPage view={'respondentes'} setView={(v)=>navigate(pathFor(v))} questions={questions} setQuestions={setQuestions} responses={responses} addResponse={handleSubmitResponse} />} />
            <Route path="/" element={<QuestionarioPage view={'responder'} setView={(v)=>navigate(pathFor(v))} questions={questions} setQuestions={setQuestions} responses={responses} addResponse={handleSubmitResponse} />} />
          </Routes>
        </main>
      </div>
      <Footer />
    </div>
  );
}

export default App;
