import React, { useState } from 'react';
import injectMenuStyles from './styles';
import type { UserRole } from '../../types/user';

injectMenuStyles();

type View = 'criar' | 'responder' | 'resultados' | 'respondentes';

export default function Menu({ current, onChange, userRole }: { current?: string; onChange?: (v: any) => void; userRole?: UserRole }) {
  const [collapsed, setCollapsed] = useState(false);

  // Manager menu items
  const managerItems = [
    { key: 'gerenciar', icon: 'üëî', label: 'Gerenciar', path: '/gerenciar' },
    { key: 'criar', icon: '‚úèÔ∏è', label: 'Criar', path: '/criar' },
    { key: 'resultados', icon: 'üìä', label: 'Resultados', path: '/resultados' },
    { key: 'respondentes', icon: 'üë•', label: 'Respondentes', path: '/respondentes' },
  ];

  // Cliente menu items
  const clienteItems = [
    { key: 'home', icon: 'üè†', label: 'In√≠cio', path: '/home' },
    { key: 'responder', icon: 'üìù', label: 'Question√°rios', path: '/responder' },
    { key: 'meus-resultados', icon: 'üìä', label: 'Minhas Respostas', path: '/meus-resultados' },
  ];

  const items = userRole === 'manager' ? managerItems : clienteItems;

  return (
    <aside className={`sr-menu ${collapsed ? 'collapsed' : 'expanded'}`}>
      <div className="sr-top">
        <div className="sr-brand" onClick={() => setCollapsed(c => !c)} style={{ cursor: 'pointer' }}>
          <div className="logo" />
          <div className="brand-text">Question√°rios</div>
        </div>
      </div>

      <nav className="sr-nav">
        {items.map(item => (
          <button key={item.key} className={current === item.path ? 'nav-item active' : 'nav-item'} onClick={() => onChange && onChange(item.path)}>
            <span className="icon" aria-hidden>{item.icon}</span>
            <span className="label">{item.label}</span>
          </button>
        ))}
      </nav>

      <button className="sr-toggle" onClick={() => setCollapsed(c => !c)} aria-label={collapsed ? 'Expandir menu' : 'Recolher menu'}>
        {collapsed ? '‚ñ∂' : '‚óÄ'}
      </button>

      <div className="sr-footer">SEBRAE</div>
    </aside>
  );
}
